import React from 'react';
import './TillForm.css';

function TillForm({ formData, setFormData }) {
  const { cash, cent } = formData;

  const handleChange = (e) => {
    const { name, value } = e.target;
    const [section, denomination] = name.split('.');
    const inputValue = value.trim();

    setFormData((prevFormData) => ({
      ...prevFormData,
      [section]: {
        ...prevFormData[section],
        [denomination]: inputValue,
      },
    }));
  };

  const calculateTotal = () => {
    let total = 0;

    for (const denomination in cash) {
      const quantity = parseFloat(cash[denomination]) || 0;
      total += denomination * quantity;
    }

    for (const denomination in cent) {
      const quantity = parseFloat(cent[denomination]) || 0;
      total += denomination * quantity / 100;
    }

    return total.toFixed(2);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    // Handle form submission logic here (e.g., saving to database)
  };

  const handleReset = () => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      cash: Object.fromEntries(Object.keys(prevFormData.cash).map((denomination) => [denomination, ''])),
      cent: Object.fromEntries(Object.keys(prevFormData.cent).map((denomination) => [denomination, ''])),
    }));
  };

  return (
    <div className="form-grid">
      {/* Form fields for cash */}
      <div className="form-section">
        {Object.entries(cash).map(([denomination, quantity]) => (
          <div key={denomination} className="form-row">
            <label>{`$${denomination}:`}</label>
            <input
              type="number"
              name={`cash.${denomination}`}
              value={quantity}
              onChange={handleChange}
            />
            <span>${(denomination * quantity).toFixed(2)}</span>
          </div>
        ))}
      </div>

      {/* Form fields for cents */}
      <div className="form-section">
        <label>Cents</label>
        {Object.entries(cent).map(([denomination, quantity]) => (
          <div key={denomination} className="form-row">
            <label>{`$${denomination}:`}</label>
            <input
              type="number"
              name={`cent.${denomination}`}
              value={quantity}
              onChange={handleChange}
            />
            <span>${(denomination * quantity / 100).toFixed(2)}</span>
          </div>
        ))}
      </div>

      {/* Buttons */}
      <div className="form-row">
        <button type="submit" onClick={handleSubmit}>Submit</button>
        <button type="button" onClick={handleReset}>Reset</button>
      </div>

      {/* Display total cash */}
      <div className="total">
        <p>Total Cash In: ${calculateTotal()}</p>
      </div>
    </div>
  );
}

export default TillForm;
