import React, { useState } from 'react';
import TillForm from './TillForm';
import LottoForm from './LottoForm';
import './DualForms.css';

function DualForms() {
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]); // Current date in 'YYYY-MM-DD' format
  const [name, setName] = useState(''); // Name input state

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  return (
    <div className="dual-forms-container">
      {/* TillForm with date and name input fields */}
      <div className="form-container">
        <h2>Daily Closing Tills Form</h2>
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={handleNameChange}
        />
        <TillForm date={date} name={name} />
      </div>

      {/* LottoForm with date and name input fields */}
      <div className="form-container">
        <h2>Daily Closing Lotto Form</h2>
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={handleNameChange}
        />
        <LottoForm date={date} name={name} />
      </div>
    </div>
  );
}

export default DualForms;
