import React, { useState } from 'react';
import './TillForm.css';

function TillForm() {
  const [formData, setFormData] = useState({
    name: '',
    date: '',
    cash: {
      100: '',
      50: '',
      20: '',
      10: '',
      5: '',
      2: '',
      1: '',
      '0.25': '',
      '0.10': '',
      '0.05': ''
    }
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    const [type, denomination] = name.split('.');
    const inputValue = value.trim(); // Remove leading and trailing spaces
    setFormData(prevState => ({
      ...prevState,
      cash: {
        ...prevState.cash,
        [denomination]: inputValue
      }
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData); // Log form data
  };

  const calculateTotal = () => {
    let total = 0;
    for (const denomination in formData.cash) {
      const quantity = parseFloat(formData.cash[denomination]) || 0; // Convert to number or default to 0
      total += denomination * quantity;
    }
    return total.toFixed(2);
  };

  return (
    <div className="form-container">
      <h2>Daily Closing Tills Form</h2>
      <form onSubmit={handleSubmit} className="form-grid">
        <div className="form-section">
          {Object.entries(formData.cash).map(([denomination, quantity]) => (
            <div key={denomination} className="form-row">
              <label>{`$${denomination}:`}</label>
              <input
                type="number"
                name={`cash.${denomination}`}
                value={quantity === '' ? '' : parseFloat(quantity)} // Set value to empty string if quantity is empty
                onChange={handleChange}
              />
              <span>${(denomination * (parseFloat(quantity) || 0)).toFixed(2)}</span>
            </div>
          ))}
        </div>
        <button type="submit">Submit</button>
      </form>
      <div className="total">
        <p>Total Cash In: ${calculateTotal()}</p>
      </div>
    </div>
  );
}

export default TillForm;
