import React, { useState } from 'react';
import './TillForm.css';

function TillForm() {
  const [formData, setFormData] = useState({
    name: '',
    date: '',
    cash: {
      100: 0,
      50: 0,
      20: 0,
      10: 0,
      5: 0,
      2: 0,
      1: 0,
      '0.25': 0,
      '0.10': 0,
      '0.05': 0
    }
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    const [type, denomination] = name.split('.');
    const inputValue = Number(value);
    // Check if the value is a positive number
    if (!isNaN(inputValue) && inputValue >= 0) {
      setFormData(prevState => ({
        ...prevState,
        [type]: {
          ...prevState[type],
          [denomination]: inputValue
        }
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData); // Log form data
  };

  const calculateTotal = () => {
    let total = 0;
    for (const denomination in formData.cash) {
      total += denomination * formData.cash[denomination];
    }
    return total.toFixed(2);
  };

  return (
    <div className="form-container">
      <h2>Daily Closing Tills Form</h2>
      <form onSubmit={handleSubmit} className="form-grid">
        <div className="form-section">
          {Object.entries(formData.cash).map(([denomination, quantity]) => (
            <div key={denomination} className="form-row">
              <label>{`$${denomination}:`}</label>
              <input
                type="number"
                name={`cash.${denomination}`} // Remove the replace method
                value={quantity}
                onChange={handleChange}
              />
              <span>${(denomination * quantity).toFixed(2)}</span>
            </div>
          ))}
        </div>
        <button type="submit">Submit</button>
      </form>
      <div className="total">
        <p>Total Cash In: ${calculateTotal()}</p>
      </div>
    </div>
  );
}

export default TillForm;
