// TillForm.js

import React, { useState } from 'react';
import './TillForm.css';

function TillForm() {
  const [formData, setFormData] = useState({
    name: '',
    date: '',
    bills: {
      100: 0,
      50: 0,
      20: 0,
      10: 0,
      5: 0
    },
    coins: {
      2: 0,
      1: 0,
      0.25: 0,
      0.1: 0,
      0.05: 0
    }
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your logic to handle form submission (e.g., save data)
    console.log(formData);
  };

  const calculateTotal = () => {
    let total = 0;
    for (const denomination in formData.bills) {
      total += denomination * formData.bills[denomination];
    }
    for (const denomination in formData.coins) {
      total += denomination * formData.coins[denomination];
    }
    return total.toFixed(2);
  };

  return (
    <div className="form-container">
      <h2>Daily Closing Tills Form</h2>
      <form onSubmit={handleSubmit} className="form-grid">
        <div className="form-section">
          <h3>Bills</h3>
          {Object.entries(formData.bills).map(([denomination, quantity]) => (
            <div key={denomination} className="form-row">
              <label>{`$${denomination}:`}</label>
              <input
                type="number"
                name={`bills.${denomination}`}
                value={quantity}
                onChange={handleChange}
              />
              <span>Total: ${(denomination * quantity).toFixed(2)}</span>
            </div>
          ))}
        </div>
        <div className="form-section">
          <h3>Coins</h3>
          {Object.entries(formData.coins).map(([denomination, quantity]) => (
            <div key={denomination} className="form-row">
              <label>{`$${denomination}:`}</label>
              <input
                type="number"
                name={`coins.${denomination}`}
                value={quantity}
                onChange={handleChange}
              />
              <span>${(denomination * quantity).toFixed(2)}</span>
            </div>
          ))}
        </div>
        <button type="submit">Submit</button>
      </form>
      <div className="total">
        <p>Total Cash In: ${calculateTotal()}</p>
      </div>
    </div>
  );
}

export default TillForm;
