import React, { useState } from 'react';
import './LottoForm.css';

function LottoForm() {
  // State to hold the form data
  const [formData, setFormData] = useState({
    lotto: {
      open: {},
      add: {},
      total: {},
      close: {},
      sold: {},
      dollar: {},
    },
  });

  // Function to handle input changes
  const handleChange = (e, category) => {
    const { name, value } = e.target;
    const denomination = name;
    const quantity = parseInt(value, 10) || 0;

    setFormData((prevState) => ({
      ...prevState,
      lotto: {
        ...prevState.lotto,
        [category]: {
          ...prevState.lotto[category],
          [denomination]: quantity,
        },
      },
    }));
  };

  // Function to calculate totals
  const calculateTotals = () => {
    const { open, add, close, sold } = formData.lotto;
    const total = {};
    const dollar = {};

    const denominations = ['$1', '$2', '$3', '$5', '$10', '$20', '$25', '$30', '$50'];

    denominations.forEach((denomination) => {
      total[denomination] = (open[denomination] || 0) + (add[denomination] || 0);
      close[denomination] = total[denomination] - (sold[denomination] || 0);
      dollar[denomination] = close[denomination] * parseInt(denomination.slice(1), 10);
    });

    return { total, dollar };
  };

  const { total, dollar } = calculateTotals();

  return (
    <div className="lotto-form">
      <h2>Lotto Form</h2>
      <form>
        <table>
          <thead>
            <tr>
              <th>Denomination</th>
              <th>Open</th>
              <th>Add</th>
              <th>Total</th>
              <th>Close</th>
              <th>Sold</th>
              <th>Dollar</th>
            </tr>
          </thead>
          <tbody>
            {['$1', '$2', '$3', '$5', '$10', '$20', '$25', '$30', '$50'].map((denomination) => (
              <tr key={denomination}>
                <td>{denomination}</td>
                <td>
                  <input
                    type="number"
                    name={denomination}
                    value={formData.lotto.open[denomination] || ''}
                    onChange={(e) => handleChange(e, 'open')}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    name={denomination}
                    value={formData.lotto.add[denomination] || ''}
                    onChange={(e) => handleChange(e, 'add')}
                  />
                </td>
                <td>{total[denomination]}</td>
                <td>{formData.lotto.close[denomination]}</td>
                <td>
                  <input
                    type="number"
                    name={denomination}
                    value={formData.lotto.sold[denomination] || ''}
                    onChange={(e) => handleChange(e, 'sold')}
                  />
                </td>
                <td>{dollar[denomination]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </form>
    </div>
  );
}

export default LottoForm;
