import React, { useState } from 'react';
import './DualForms.css';
import TillForm from './TillForm';
import LottoForm from './LottoForm';

function DualForms() {
  const [formData, setFormData] = useState({
    name: '',
    date: new Date().toISOString().split('T')[0], // Current date in 'YYYY-MM-DD' format
  });

  const handleNameChange = (e) => {
    setFormData({ ...formData, name: e.target.value });
  };

  const handleDateChange = (e) => {
    setFormData({ ...formData, date: e.target.value });
  };

  return (
    <div className="dual-forms-container">
      {/* Left side: TillForm */}
      <div className="form-container">
        <h2>Daily Closing Tills Form</h2>
        <input
          type="date"
          value={formData.date}
          onChange={handleDateChange}
        />
        <input
          type="text"
          placeholder="Name"
          value={formData.name}
          onChange={handleNameChange}
        />
        <TillForm formData={formData} setFormData={setFormData} />
      </div>

      {/* Right side: LottoForm */}
      <div className="form-container">
        <h2>Daily Closing Lotto Form</h2>
        <input
          type="date"
          value={formData.date}
          onChange={handleDateChange}
        />
        <input
          type="text"
          placeholder="Name"
          value={formData.name}
          onChange={handleNameChange}
        />
        <LottoForm formData={formData} setFormData={setFormData} />
      </div>
    </div>
  );
}

export default DualForms;
