import React, { useState } from 'react';
import './TillForm.css'
function TillForm() {
  const [formData, setFormData] = useState({
    name: '',
    date: '',
    bills: {
      100: 0,
      50: 0,
      20: 0,
      10: 0,
      5: 0
    },
    coins: {
      0.25: 0,
      0.10: 0,
      0.05: 0,
      1: 0,
      2: 0
    },
    cashIn: 0
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    // Clear form fields
    setFormData({
      name: '',
      date: '',
      bills: {
        100: 0,
        50: 0,
        20: 0,
        10: 0,
        5: 0
      },
      coins: {
        0.25: 0,
        0.10: 0,
        0.05: 0,
        1: 0,
        2: 0
      },
      cashIn: 0
    });
  };

  return (
<div className="form-container">
      <h2>Daily Closing Tills Form</h2>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} />

        <label>Date:</label>
        <input type="date" name="date" value={formData.date} onChange={handleChange} />
        {/* Input fields for bills */}
        <label htmlFor="bills">Bills:</label>
        {Object.entries(formData.bills).map(([bill, count]) => (
          <div key={bill}>
            <label htmlFor={`bill-${bill}`}>{`$${bill}:`}</label>
            <input
              type="number"
              id={`bill-${bill}`}
              name={`bills.${bill}`}
              value={count}
              onChange={handleChange}
              min="0"
              step="1"
            />
          </div>
        ))}

        {/* Input fields for coins */}
        <label htmlFor="coins">Coins:</label>
        {Object.entries(formData.coins).map(([coin, count]) => (
          <div key={coin}>
            <label htmlFor={`coin-${coin}`}>{`$${coin}:`}</label>
            <input
              type="number"
              id={`coin-${coin}`}
              name={`coins.${coin}`}
              value={count}
              onChange={handleChange}
              min="0"
              step="1"
            />
          </div>
        ))}

        {/* Input field for cashIn */}
        <label htmlFor="cashIn">Cash In:</label>
        <input type="number" id="cashIn" name="cashIn" value={formData.cashIn} onChange={handleChange} min="0" step="0.01" required />

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default TillForm;
