import React from 'react';
import './LottoForm.css';

function LottoForm({ formData, setFormData }) {
  const { denominations } = formData;

  const handleChange = (e, denomination, type) => {
    const value = parseFloat(e.target.value) || 0;

    setFormData((prevFormData) => ({
      ...prevFormData,
      denominations: {
        ...prevFormData.denominations,
        [denomination]: {
          ...prevFormData.denominations[denomination],
          [type]: value,
          sold: (prevFormData.denominations[denomination].add - value) || 0,
          dollar: denomination * ((prevFormData.denominations[denomination].add - value) || 0),
        },
      },
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    // Handle form submission logic here (e.g., saving to database)
  };

  const totalDollar = Object.values(denominations)
    .reduce((acc, { dollar }) => acc + dollar, 0)
    .toFixed(2);

  return (
    <div className="lotto-form">
      <form onSubmit={handleSubmit}>
        <table>
          <thead>
            <tr>
              <th>Lotto</th>
              <th>Open</th>
              <th>Add</th>
              <th>Close</th>
              <th>Sold</th>
              <th>Dollar</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(denominations).map(([denomination, data]) => (
              <tr key={denomination}>
                <td>{`$${denomination}`}</td>
                <td>
                  <input
                    type="number"
                    value={data.open}
                    onChange={(e) => handleChange(e, denomination, 'open')}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={data.add}
                    onChange={(e) => handleChange(e, denomination, 'add')}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={data.close}
                    onChange={(e) => handleChange(e, denomination, 'close')}
                  />
                </td>
                <td>{data.sold}</td>
                <td>{data.dollar.toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
        {/* Total dollar */}
        <div className="total">
          <p>Total Dollar: ${totalDollar}</p>
        </div>
        {/* Submit button */}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default LottoForm;
