import React, { useState } from 'react';
import './LottoForm.css';
import { getFirestore, collection, addDoc } from "firebase/firestore";
import { app } from './firebase'; // Import the app instance

// Initialize Firestore using the Firebase app
const db = getFirestore(app);

function LottoForm() {
    // Define the denominations and initial form data
    const denominations = [1, 2, 3, 5, 10, 20, 25, 30, 50];
    const [formData, setFormData] = useState(
        denominations.reduce((acc, denomination) => {
            acc[denomination] = {
                open: '',
                add: '',
                close: '',
                sold: '',
                dollar: 0
            };
            return acc;
        }, {})
    );

    // Handle input changes
    const handleChange = (e, denomination, type) => {
        const value = parseFloat(e.target.value) || 0;
        const updatedFormData = { ...formData };

        // Update the appropriate form field
        updatedFormData[denomination][type] = value;

        // Calculate sold and dollar based on changes to add and close
        if (type === 'add' || type === 'close') {
            updatedFormData[denomination].sold =
                updatedFormData[denomination].add - updatedFormData[denomination].close;
            updatedFormData[denomination].dollar =
                denomination * updatedFormData[denomination].sold;
        }

        // Update form data state
        setFormData(updatedFormData);
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Reference to the 'lottoForm' collection in Firestore
        const lottoFormRef = collection(db, 'lottoForm');

        try {
            // Add the form data to Firestore
            await addDoc(lottoFormRef, formData);
            console.log('Form data stored in Firestore:', formData);

            // Optionally clear the form data or reset the state
            setFormData(
                denominations.reduce((acc, denomination) => {
                    acc[denomination] = {
                        open: '',
                        add: '',
                        close: '',
                        sold: '',
                        dollar: 0
                    };
                    return acc;
                }, {})
            );

        } catch (error) {
            console.error('Error storing form data in Firestore:', error);
        }
    };

    // Calculate the sum of the Dollar column
    const totalDollar = Object.values(formData)
        .reduce((acc, denominationData) => acc + denominationData.dollar, 0)
        .toFixed(2);

    return (
        <div className="lotto-form">
            <h2>Daily Closing Lotto Form</h2>
            <form onSubmit={handleSubmit}>
                <table>
                    <thead>
                        <tr>
                            <th>Lotto</th>
                            <th>Open</th>
                            <th>Add</th>
                            <th>Close</th>
                            <th>Sold</th>
                            <th>Dollar</th>
                        </tr>
                    </thead>
                    <tbody>
                        {denominations.map((denomination) => (
                            <tr key={denomination}>
                                <td>{`$${denomination}`}</td>
                                <td>
                                    <input
                                        type="number"
                                        value={formData[denomination].open}
                                        onChange={(e) => handleChange(e, denomination, 'open')}
                                    />
                                </td>
                                <td>
                                    <input
                                        type="number"
                                        value={formData[denomination].add}
                                        onChange={(e) => handleChange(e, denomination, 'add')}
                                    />
                                </td>
                                <td>
                                    <input
                                        type="number"
                                        value={formData[denomination].close}
                                        onChange={(e) => handleChange(e, denomination, 'close')}
                                    />
                                </td>
                                <td>{formData[denomination].sold}</td>
                                <td>{formData[denomination].dollar.toFixed(2)}</td>
                            </tr>
                        ))}
                        {/* Last row with blank input boxes and total */}
                        <tr>
                            <td colSpan="2">
                                {/* Grouping blank input boxes */}
                                <div className="input-group">
                                    <label>Pay-out</label>
                                    <input type="number" placeholder="Enter amount" />
                                </div>
                                <div className="input-group">
                                    <label>Lottery sale</label>
                                    <input type="number" placeholder="Enter amount" />
                                </div>
                            </td>
                            <td colSpan="2">Total</td>
                            <td colSpan="2">{totalDollar}</td>
                        </tr>
                    </tbody>
                </table>
                <button type="submit">Submit</button>
            </form>
        </div>
    );
}

export default LottoForm;
